import React from 'react'
import Select from 'react-select'

function CustomSelect(props) {
  return (
    <Select
    options={props.options}
    defaultValue={props.options[0]}
    theme={(theme) => ({
      ...theme,
      borderRadius: 0,
    })}
    styles={{
      option: (base) => ({
        ...base,
        color: 'white',
        backgroundColor: '#353535',
      }),
      menuList: (base) => ({
        ...base,
        backgroundColor: '#353535',
        border: 'none',
      }),
    }}
  />
  )
}

export default CustomSelect
