import React, {useState} from 'react'
import Slot from './Slot'
import Modal from './UI/modal/Modal'
import ItemPicker from './ItemPicker'

function Equipment() {
  const leftCol = [
    'Голова',
    'Шея',
    'Плечи',
    'Спина',
    'Грудь',
    'Рубашка',
    'Накидка',
    'Запястья',
    'Правая рука',
    'Левая рука',
  ]
  const rightCol = [
    'Кисти рук',
    'Пояс',
    'Ноги',
    'Стопы',
    'Палец 1',
    'Палец 2',
    'Аксессуар 1',
    'Аксессуар 2',
    'Дальний бой',
  ]
  const [modal, setModal] = useState(false);
  const [slot, setSlot] = useState('');
  const [eqip, setEqip] = useState({
    head: null,
    neck: null,
    shoulders: null,
    back: null,
    chest: null,
    shirt: null,
    tabard: null,
    wrist: null,
    mainHand: null,
    offHand: null,
    hands: null,
    waist: null,
    legs: null,
    feet: null,
    finger1: null,
    finger2: null,
    trinket1: null,
    trinket2: null,
    ranged: null,
  });

  const pickItem = (slot, item) => {
    setEqip({...eqip, slot: item});
    console.log(eqip);
  }

  return (
    <div className='Equipment'>
        <div className='equip-column'>
          {leftCol.map((name, index) =>
            <Slot key={index} name={name} item={eqip[name]}
              setModal={
                (slotName) => {
                  setModal(true);
                  setSlot(slotName);
                }
              }
            />
          )}
        </div>
        <div className='equip-column'>
          {rightCol.map((name, index) =>
            <Slot key={index} name={name} reverse={'row-reverse'} item={eqip[name]}
              setModal={(slotName) => {
                setModal(true);
                setSlot(slotName);
                }
              }
            />
          )}
        </div>
        <Modal visible={modal} setVisible={setModal}>
          <ItemPicker slot={slot} pickItem={pickItem}/>
        </Modal>
    </div>
    
  )
}

export default Equipment
