import React, {useState} from 'react'
import Slot from './Slot'
import Modal from './UI/modal/Modal'
import ItemPicker from './ItemPicker'

function Equipment() {
  const leftCol = [
    'Голова',
    'Шея',
    'Плечи',
    'Спина',
    'Грудь',
    'Рубашка',
    'Накидка',
    'Запястья',
    'Правая рука',
    'Левая рука',
  ]
  const rightCol = [
    'Кисти рук',
    'Пояс',
    'Ноги',
    'Стопы',
    'Палец 1',
    'Палец 2',
    'Аксессуар 1',
    'Аксессуар 2',
    'Дальний бой',
  ]
  const [modal, setModal] = useState(false);
  const [slot, setSlot] = useState('');

  return (
    <div className='Equipment'>
        <div className='equip-column'>
          {leftCol.map((name, index) =>
            <Slot key={index} name={name} setModal={(slotName) => {
              setModal(true);
              setSlot(slotName);
            }
            }/>
          )}
        </div>
        <div className='equip-column'>
          {rightCol.map((name, index) =>
            <Slot key={index} name={name} setModal={() => setModal(true)} reverse={'row-reverse'}/>
          )}
        </div>
        <Modal visible={modal} setVisible={setModal}>
          <ItemPicker slot={slot}/>
        </Modal>
    </div>
    
  )
}

export default Equipment
