import { useMemo } from 'react'

export const useItems = (items, filter) => {
    const searchedItems = useMemo(() => {
        return items.filter(item => item.name.toLowerCase().includes(filter.toLocaleLowerCase()))
    }, [filter])

    return searchedItems;
}
