import React, { useState, useEffect } from 'react'
import ItemSearch from './ItemSearch'
import ItemList from './ItemList'
import { useFetching } from '../hooks/useFetching';

function ItemPicker(props) {
  const [items, setItems] = useState([]);
  const [fetchItems, isLoading, errorItems] = useFetching(async () => {
    const response = await Service.getItems();
    setItems([...response.data])
  })

  useEffect(() => {
    fetchItems();
  }, [])

  return (
    <div className='ItemPicker'>
        <div className='ItemPicker-header'>
            <ItemSearch/>
            <div className='ItemPicker-slot'>{props.slot}</div>
        </div>
        <ItemList items={items}/>
        {isLoading &&
          <div style={{display: 'flex', justifyContent: 'center', marginTop: '50px'}}><Loader></Loader></div>
        }
    </div>
  )
}

export default ItemPicker
